mapper.select:
SELECT t1.record_category spendCategoryName,SUM(abs(amount)) total
FROM
    tb_record_detail t1
where t1.user_id = #{userId}
    and t1.record_type = #{recordType}
    and date_format(t1.occur_time, '%Y-%m') = date_format(#{date}, '%Y-%m')
    and t1.delete_time is null
    group by t1.record_category ORDER BY total desc
    LIMIT #{begin},#{end}

select t1.*, t2.name recordAccountName, t3.name sourceAccountName, t4.name targetAccountName from tb_record_detail t1 left join tb_record_account t2 on t1.record_account_id = t2.id left join tb_record_account t3 on t1.source_account_id = t3.id left join tb_record_account t4 on t1.target_account_id = t4.id where t1.user_id = #{userId}
and record_book_id = #{recordBookId}
and date_format(t1.occur_time, '%Y-%m') = date_format(#{date}, '%Y-%m')
and date_format(t1.occur_time, '%Y-%m-%d') = date_format(#{occurTime}, '%Y-%m-%d')
and (t1.record_category like CONCAT(CONCAT('%', #{keyWord}), '%') or t1.remark like CONCAT(CONCAT('%', #{keyWord}), '%'))
and t1.delete_time is null and t1.target_account_id is null -- 流出账户为空 ORDER BY t1.occur_time desc

select t1.*, t2.name recordAccountName, t3.name sourceAccountName, t4.name targetAccountName from tb_record_detail t1 left join tb_record_account t2 on t1.record_account_id = t2.id left join tb_record_account t3 on t1.source_account_id = t3.id left join tb_record_account t4 on t1.target_account_id = t4.id where t1.user_id = #{userId}
and record_account_id = #{recordAccountId}
and date_format(t1.occur_time, '%Y-%m') = date_format(#{date}, '%Y-%m')
and date_format(t1.occur_time, '%Y-%m-%d') = date_format(#{occurTime}, '%Y-%m-%d')
and (t1.record_category like CONCAT(CONCAT('%', #{keyWord}), '%') or t1.remark like CONCAT(CONCAT('%', #{keyWord}), '%'))
and t1.delete_time is null ORDER BY t1.occur_time desc
